import { Component, TemplateRef } from '@angular/core';
import { BsModalRef, BsModalService } from 'ngx-bootstrap/modal';
import { ToastrService } from 'ngx-toastr';

import { EventoService } from '../../services/evento.service';
import { Evento } from '../../models/Evento';
import { NgxSpinnerService } from 'ngx-spinner';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss'],
  //providers: [EventoService]
})
export class EventosComponent {

  public modalRef?: BsModalRef;
  public eventos: Evento[] = [];
  public eventosFiltrados: Evento[] = [];
  public widthImg: number = 80;
  public marginImg: number = 2;

  public imgToggleBtn : boolean = true;
  public toggleBtnIcon = "fa-solid fa-eye-slash";
  private _filtroLista: string = '';

  public get filtroLista(): string {
  return this._filtroLista;
  };

  public set filtroLista(value:string) {
  this._filtroLista = value;
  this.eventosFiltrados = this.filtroLista ? this.filtrarEventos(this.filtroLista) : this.eventos;
  }

  public filtrarEventos(filtrarPor: string): Evento[] {
      filtrarPor = filtrarPor.toLocaleLowerCase();
      return this.eventos.filter(
        (evento: any) => evento.tema.toLocaleLowerCase().indexOf(filtrarPor) !== -1 ||
        evento.local.toLowerCase().indexOf(filtrarPor) !== -1
      )
  }

  constructor(private eventoService: EventoService,
              private modalService: BsModalService,
              private toastr: ToastrService,
              private spinner: NgxSpinnerService) {}

  public ngOnInit(): void {
    this.spinner.show();
    this.getEventos();
    }

  public toglleBtnImg() : void {
    this.imgToggleBtn = !this.imgToggleBtn;
    if (this.imgToggleBtn == true ) {
      this.toggleBtnIcon = "fa-solid fa-eye-slash"
    } else {
      this.toggleBtnIcon = "fa-solid fa-eye"
    }
  }

  public getEventos(): void {
    this.eventoService.getEventos().subscribe({
      next: (_eventos: Evento[]) => {
      this.eventos = _eventos,
        this.eventosFiltrados = this.eventos
      },
      error: (error: any) => {
        this.spinner.hide();
        this.toastr.error('Erro ao carregar Eventos.', 'Erro');
      },
      complete: () => this.spinner.hide()
    });
  }


  public openModal(template: TemplateRef<any>) : void {
    this.modalRef = this.modalService.show(template, { class: 'modal-sm' });
  }

  public confirm(): void {
    this.modalRef?.hide();
    this.toastr.success('Evento deletado com sucesso.', 'Deletado');
  }

  public decline(): void {
    this.modalRef?.hide();
  }

}
<div class="container mt-4">
  <app-titulo [titulo]="'Eventos'"
  [subtitulo]="'Desde 2025'"
  [iconPage]="'fa fa-calendar-alt'"
  [botaoListar] = 'true'
  ></app-titulo>
<div class="card rouded shadow-sm p-3">
  <div class="d-flex">
    <div class="flex-fi">
      <div class="form-group mb-2">
        <label class="mr-2">Filtro:</label>
        <input
        [(ngModel)]="filtroLista"
        type="text"
        class="form-control mr-2"
        placeholder="buscar">
      </div>
    </div>
    <div>
      <a class="d-flex btn btn-outline-primary" href="#">
        <i class="fa fa-plus-circle my-2"></i>
        <b class="ml-1 d-none d-sm-block">Novo</b>
      </a>
    </div>
  </div>

  <table class="table table-striped">
    <thead class="thead-dark">
      <tr>
        <th>
          <button class="btn btn-outline-light" (click)="toglleBtnImg()">
            <i class="{{toggleBtnIcon}}"></i>
          </button>
        </th>
        <th>#</th>
        <th>Tema</th>
        <th>Local</th>
        <th>Data</th>
        <th>Qtd Pessoas</th>
        <th>Lote</th>
        <th>Opções</th>
      </tr>
    </thead>
    <tbody *ngIf="eventos && eventos.length">
      <tr *ngFor="let evento of eventosFiltrados">
        <td>
          <img [collapse]="!imgToggleBtn"
                [isAnimated]="true"
          src="./../../assets/images/{{evento.imagemURL}}"
           alt=""
          [style.width.px]="widthImg"
          [style.margin.px]="marginImg"
           >
        </td>
        <td>{{evento.id}}</td>
        <td>{{evento.tema}}</td>
        <td>{{evento.local}}</td>
        <td>{{evento.dataEvento | DateFormatPipe}}</td>
        <td>{{evento.qtdPessoas}}</td>
        <td>{{evento.lotes[0].nome}} </td>
        <td>
          <button class="btn btn-primary btn-sm mr-2" tooltip="Editar"><i class="fa-solid fa-pen-to-square"></i></button>
          <button class="btn btn-danger btn-sm" tooltip="Excluir" (click)="openModal(template)"><i class="fa-solid fa-trash"></i></button>
        </td>
      </tr>
    </tbody>
  <tfoot *ngIf="eventos.length == 0">
    <tr>
      <td colspan="8" class="text-center">
        <h4>Nenhum evento localizado!</h4>
      </td>
    </tr>
  </tfoot>
  </table>
</div>
</div>

<!-- {{eventos | json}} -->

<ng-template #template>
  <div class="modal-body text-center">
    <p>Quer mesmo excluir o regstro?</p>
    <button type="button" class="btn btn-default" (click)="confirm()" >Sim</button>
    <button type="button" class="btn btn-primary" (click)="decline()" >Não</button>
  </div>
</ng-template>
