import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-eventos',
  templateUrl: './eventos.component.html',
  styleUrls: ['./eventos.component.scss']
})
export class EventosComponent {

  public eventos: any = [];
  widthImg: number = 80;
  marginImg: number = 2;

  imgToggleBtn : boolean = true;
  toggleBtnIcon = "fa-solid fa-eye-slash";


  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.getEventos();
  }

  public toglleBtnImg() {
    this.imgToggleBtn = !this.imgToggleBtn;
    if (this.imgToggleBtn == true ) {
      this.toggleBtnIcon = "fa-solid fa-eye-slash"
    } else {
      this.toggleBtnIcon = "fa-solid fa-eye"
    }
  }

  public getEventos(): void {
    this.http.get('https://localhost:5001/api/eventos').subscribe(
      response => this.eventos = response,
      error => console.log(error),
    );
  }

}
<h2>Eventos</h2>
<hr>
<form action="" class="form-inline">
  <div class="form-group mb-2">
    <input

    type="text"
    class="form-control mr-2"
    placeholder="buscar">
    <button class="btn btn-outline-success">Buscar</button>
  </div>
</form>
<table class="table table-striped">
  <thead class="thead-dark">
    <tr>
      <th>
        <button class="btn btn-outline-light" (click)="toglleBtnImg()">
          <i class="{{toggleBtnIcon}}"></i>
        </button>
      </th>
      <th>#</th>
      <th>Tema</th>
      <th>Local</th>
      <th>Data</th>
      <th>Qtd Pessoas</th>
      <th>Lote</th>
      <th>Opções</th>
    </tr>
  </thead>
  <tbody *ngIf="eventos && eventos.length">
    <tr *ngFor="let evento of eventos">
      <td>
        <img [collapse]="!imgToggleBtn"
              [isAnimated]="true"
        src="./../../assets/images/{{evento.imagemURL}}"
         alt=""
        [style.width.px]="widthImg"
        [style.margin.px]="marginImg"
         >
      </td>
      <td>{{evento.eventoId}}</td>
      <td>{{evento.tema}}</td>
      <td>{{evento.local}}</td>
      <td>{{evento.dataEvento}}</td>
      <td>{{evento.qtdPessoas}}</td>
      <td>{{evento.lote}}</td>
      <td></td>
    </tr>
  </tbody>
<tfoot *ngIf="!eventos">
  <tr>
    <td colspan="8" class="text-center">
      <h4>Nenhum evento localizado!</h4>
    </td>
  </tr>
</tfoot>
</table>
