import { Component, Input, OnInit } from '@angular/core';

@Component({
  selector: 'app-titulo',
  templateUrl: './titulo.component.html',
  styleUrls: ['./titulo.component.scss']
})
export class TituloComponent implements OnInit {

  @Input() titulo?: string;
  @Input() subtitulo = 'Desde 2025';
  @Input() iconPage = 'fa fa-user';
  @Input() botaoListar?: boolean;

  constructor() { }

  ngOnInit() {
  }

}

<div class="d-flex mr-auto p-3 my-3 text-white bg-secondary rounded shadow-sm">
    <div class="d-flex aling-items-center mr-auto">
      <i class="{{iconPage}}"></i>
        <div class="lh-100">
          <h2 class="mb-0">{{titulo}}</h2>
          <small>{{subtitulo}}</small>
        </div>
    <div class="p-3" *ngIf="botaoListar">
      <button class="btn btn-outline-light">Listar {{titulo}}

      </button>
    </div>
</div>
